import React, { useCallback, useEffect, useState } from "react";
import PopUp from './PopUp.jsx';
import BigPopUp from './BigPopUp.jsx';

const Main = () => {
    const maxBet = 64;
    const minBet = 1;
    const betStep = 2;
    const [balance, setBalance] = useState(100);
    const [bet, setBet] = useState(1);
    const [useEffectState, setUseEffectState] = useState(0); 
    const [number, setNumber] = useState([7,8,9]);
    const [numberSecond, setNumberSecond] = useState([3,4,5]);
    const [numberThird, setNumberThird] = useState([9,8,7]);
    const [intervalId, setIntervalId] = useState(0);
    const [modalActive, setModalActive] = useState(false);
    const [bigpopupActive, setBigpopupActive] = useState(false);
    const win = bet * 100;
    const bigWin = bet * 500;
    

    // const spin = () => {
    //     setModalActive(false);
    //     let counter = 0;
    //     const spinIterationsAmount = 10;
    //     if(intervalId === 0 && balance >= bet) {

    //         const interval = setTimeout(() => {
    //             const newNumber = [Math.floor((Math.random() * 7)) + 3, number[0], number[1]];
    //             const newNumberSecond = [Math.floor((Math.random() * 7)) + 3, numberSecond[0], numberSecond[1]];
    //             const newNumberThird = [Math.floor((Math.random() * 7)) + 3, numberThird[0], numberThird[1]];
    //             setNumber(newNumber);
    //             setNumberSecond(newNumberSecond);
    //             setNumberThird(newNumberThird);
    //             counter += 1;
    //             setIntervalId(interval);
                
    //             if (counter >= spinIterationsAmount) {
    //                 clearTimeout(interval);
    //                 setIntervalId(0);
    //                 // setUseEffectState(useEffectState + 1)
    //                 winner(newNumber[1], newNumberSecond[1], newNumberThird[1]);
    //             }
                
    //         }, 100)
    //     }
    // }

    const spin = () => {
        setModalActive(false);
        let counter = 0;
        const spinIterationsAmount = 10;
      
        if (intervalId === 0 && balance >= bet) {
          const spinIteration = () => {
            // Генерируем новые числа для первого элемента
            const newNumber = [
              Math.floor((Math.random() * 7)) + 3,
              number[0],
              number[1],
            ];
            const newNumberSecond = [
              Math.floor((Math.random() * 7)) + 3,
              numberSecond[0],
              numberSecond[1],
            ];
            const newNumberThird = [
              Math.floor((Math.random() * 7)) + 3,
              numberThird[0],
              numberThird[1],
            ];
      
            // Обновляем состояние
            setNumber(newNumber);
            setNumberSecond(newNumberSecond);
            setNumberThird(newNumberThird);
      
            // Смещаем элементы массивов
            const tempNumber = number[1];
            const tempNumberSecond = numberSecond[1];
            const tempNumberThird = numberThird[1];
      
            setNumber([number[0], number[2], tempNumber]);
            setNumberSecond([numberSecond[0], numberSecond[2], tempNumberSecond]);
            setNumberThird([numberThird[0], numberThird[2], tempNumberThird]);
      
            counter += 1;
      
            if (counter < spinIterationsAmount) {
              // Запускаем следующую итерацию через 100 мс
              const interval = setTimeout(spinIteration, 100);
              setIntervalId(interval); 
            } else {
              // Останавливаем вращение и проверяем выигрыш
              clearTimeout(intervalId); // Очищаем предыдущий таймер
              setIntervalId(0);
              // setUseEffectState(useEffectState + 1); 
              winner(newNumber[1], newNumberSecond[1], newNumberThird[1]);
            }
          };
      
          // Запускаем первую итерацию
          spinIteration();
        }
      };


    
    // useEffect(() => {
    //     if(number === 7 && numberSecond === 7 && numberThird === 7){
    //         setBigpopupActive(true)
    //         setBalance(balance + bigWin)
    //     }
    //     else if (number === numberSecond && number === numberThird) {
    //         setBalance(balance + win)
    //         setModalActive(true)
    //         setTimeout(() => {setModalActive(false)}, 2000)
    //     }
    //     else{
    //         setBalance(balance - bet)
    //     }
    // }, [useEffectState])

    const winner = (number, numberSecond, numberThird) => {
        console.log(number, numberSecond, numberThird)
        if(number === 7 && numberSecond === 7 && numberThird === 7){
            setBigpopupActive(true);
            setBalance((balance) => balance + bigWin);
        }
        else if (number === numberSecond && number === numberThird) {
            setBalance((balance) => balance + win);
            setModalActive(true);
            setTimeout(() => {setModalActive(false)}, 2000);
        }
        else{
            setBalance((balance) => balance - bet);
        }
    }
    
    
    return (
        <div>
            <div className="header">
                <div className="slots">

                    <div className="slots_num">
                        <h1 className="square square_back">{number[0]}</h1>
                        <h1 className="square square_back">{numberSecond[0]}</h1>
                        <h1 className="square square_back">{numberThird[0]}</h1>
                    </div>
                    <div className="slots_num">
                        <h1 className="square">{number[1]}</h1>
                        <h1 className="square">{numberSecond[1]}</h1>
                        <h1 className="square">{numberThird[1]}</h1>
                    </div>
                    <div className="slots_num">
                        <h1 className="square square_back">{number[2]}</h1>
                        <h1 className="square square_back">{numberSecond[2]}</h1>
                        <h1 className="square square_back">{numberThird[2]}</h1>
                    </div>

                    <div className="slots_btn">
                        <button className="btn" onClick={spin}>Spin</button>
                    </div>
                </div>
                <div className="win_balance">
                    <div className="win_condition">
                        <h3 className="win_condition_text">Win Condition</h3>
                        <h4 className="num">333</h4>
                        <h4 className="num">444</h4>
                        <h4 className="num">555</h4>
                        <h4 className="num">666</h4>
                        <h4 className="num">777</h4>
                        <h4 className="num">888</h4>
                        <h4 className="num">999</h4>
                    </div>
                    <div className="balance">
                        <h3 className="bet">balance: {balance}$</h3>
                        <h3 className="bet bet_down">bet: {bet}$</h3>
                        <div className="tringle_up" onClick={() => {
                            if(bet < maxBet){
                                setBet(bet * betStep);
                            }
                        }}>
                        </div>
                        <div className="tringle_down" onClick={() => {
                            if(bet > minBet) {
                                setBet(bet / betStep);
                            }
                        }}>

                        </div>
                    </div>
                </div>
            </div>

            <PopUp active={modalActive} winBet={win}/>
            <BigPopUp active={bigpopupActive} setActive={setBigpopupActive} betBigWin={bigWin}/>
        
        </div>
    )
}

export default Main
