import React, { useEffect, useState } from "react"

const Main = () => {
    const [balance, setBalance] = useState(100)
    const bet = 1;
    const [number, setNumber] = useState(0)
    const [numberSecond, setNumberSecond] = useState(0)
    const [numberThird, setNumberThird] = useState(0)
    const [intervalId, setIntervalId] = useState(0)
    const spin = () => {
        let counter = 0; 
        if(intervalId === 0) {
            const interval = setInterval(() => {
                if (interval < 10) {
                    setNumber(Math.floor((Math.random() * 7)) + 3)
                    setNumberSecond(Math.floor((Math.random() * 7)) + 3)
                    setNumberThird(Math.floor((Math.random() * 7)) + 3)
                    counter += 1;
                    setIntervalId(interval)
                }
                else {
                    clearInterval(interval)
                    setIntervalId(0)
                }
            },100)
            if (number === numberSecond && numberThird) {
                setBalance(balance + 100)
            }
            else{
                setBalance(balance - bet)
            }
        }
    }
    
  return (
    <div>
        <div className="header">
            <div className="slots">
                <div className="slots_num">
                    <h1 className="square">{number}</h1>
                    <h1 className="square">{numberSecond}</h1>
                    <h1 className="square">{numberThird}</h1>
                </div>
                <div className="slots_btn">
                    <button className="btn" onClick={spin}>Spin</button>
                </div>
            </div>
            <div className="win_balance">
                <div className="win_condition">
                    <h3 className="win_condition_text">Win Condition</h3>
                    <h4 className="num">333</h4>
                    <h4 className="num">444</h4>
                    <h4 className="num">555</h4>
                    <h4 className="num">666</h4>
                    <h4 className="num">777</h4>
                    <h4 className="num">888</h4>
                    <h4 className="num">999</h4>
                </div>
                <div className="balance">
                    <h3 className="bet">balance: {balance}$</h3>
                    <h3 className="bet">bet: {bet}$</h3>
                </div>
            </div>
        </div>
      
    </div>
  )
}

export default Main
