import React, { useCallback, useEffect, useState } from "react"
import PopUp from './PopUp.jsx'
import BigPopUp from './BigPopUp.jsx'

const Main = () => {
    const bigWin = 500;
    const win = 100;
    const [balance, setBalance] = useState(100)
    const [bet, setBet] = useState(1);
    const [useEffectState, setUseEffectState] = useState(0); 
    const [number, setNumber] = useState(0)
    const [numberSecond, setNumberSecond] = useState('0')
    const [numberThird, setNumberThird] = useState('0')
    const [intervalId, setIntervalId] = useState(0)
    const [modalActive, setModalActive] = useState(false)
    const [bigpopupActive, setBigpopupActive] = useState(false)
    const arrNum = [3,4,5,6,7,8,9];

    const spin = () => {
        setModalActive(false)
        let counter = 0;
        const spinIterationsAmount = 10;
        if(intervalId === 0 && balance >= bet) {
            const interval = setInterval(() => {
                const newNumber = Math.floor((Math.random() * 7)) + 3;
                const newNumberSecond = Math.floor((Math.random() * 7)) + 3;
                const newNumberThird = Math.floor((Math.random() * 7)) + 3;
                setNumber(newNumber);
                setNumberSecond(newNumberSecond);
                setNumberThird(newNumberThird);
                counter += 1;
                setIntervalId(interval);
                
                if (counter >= spinIterationsAmount) {
                    clearInterval(interval)
                    setIntervalId(0)
                    // setUseEffectState(useEffectState + 1)
                    winner(newNumber, newNumberSecond, newNumberThird);
                }
                
            },100)
        }
    }

    // useEffect(() => {
    //     if(number === 7 && numberSecond === 7 && numberThird === 7){
    //         setBigpopupActive(true)
    //         setBalance(balance + bigWin)
    //     }
    //     else if (number === numberSecond && number === numberThird) {
    //         setBalance(balance + win)
    //         setModalActive(true)
    //         setTimeout(() => {setModalActive(false)}, 2000)
    //     }
    //     else{
    //         setBalance(balance - bet)
    //     }
    // }, [useEffectState])

    const winner = (number, numberSecond, numberThird) => {
        console.log(number, numberSecond, numberThird)
        if(number === 7 && numberSecond === 7 && numberThird === 7){
            setBigpopupActive(true)
            setBalance((balance) => balance + bigWin);
        }
        else if (number === numberSecond && number === numberThird) {
            setBalance((balance) => balance + win);
            setModalActive(true)
            setTimeout(() => {setModalActive(false)}, 2000)
        }
        else{
            setBalance((balance) => balance - bet);
        }
    }
    
    
    return (
        <div>
            <div className="header">
                <div className="slots">
                    <div className="slots_num">
                        <h1 className="square square_back">{number}</h1>
                        <h1 className="square square_back">{numberSecond}</h1>
                        <h1 className="square square_back">{numberThird}</h1>
                    </div>
                    <div className="slots_num">
                        <h1 className="square">{number}</h1>
                        <h1 className="square">{numberSecond}</h1>
                        <h1 className="square">{numberThird}</h1>
                    </div>
                    <div className="slots_num">
                        <h1 className="square square_back">{number}</h1>
                        <h1 className="square square_back">{numberSecond}</h1>
                        <h1 className="square square_back">{numberThird}</h1>
                    </div>
                    <div className="slots_btn">
                        <button className="btn" onClick={spin}>Spin</button>
                    </div>
                </div>
                <div className="win_balance">
                    <div className="win_condition">
                        <h3 className="win_condition_text">Win Condition</h3>
                        <h4 className="num">333</h4>
                        <h4 className="num">444</h4>
                        <h4 className="num">555</h4>
                        <h4 className="num">666</h4>
                        <h4 className="num">777</h4>
                        <h4 className="num">888</h4>
                        <h4 className="num">999</h4>
                    </div>
                    <div className="balance">
                        <h3 className="bet">balance: {balance}$</h3>
                        <h3 className="bet bet_down">bet: {bet}$</h3>
                        <div className="tringle_up" onClick={() => bet + 2}></div>
                        <div className="tringle_down"></div>
                    </div>
                </div>
            </div>

            <PopUp active={modalActive}/>
            <BigPopUp active={bigpopupActive} setActive={setBigpopupActive}/>
        
        </div>
    )
}

export default Main
